// A Hello World! program in C#.
using System;
using System.Linq;
namespace ConsoleApp1
{
    class Dayone
    {
        private static void Main()
        {
            //Part1 1390
            string source = "";
            Int32[] array = source.Select(x => Int32.Parse(x.ToString())).ToArray();
            // Keep the console window open in debug mode.i'i'i'b'b
            int total = 0; int a = 0; int first = 0; int last = 0;
            last = array.Last();
            first = array.First();
            foreach (var item in array)
            {
                total = item == a ? total + item : total;
                a = item;
            }

            total = last == first ? total + last : total;
            Console.WriteLine("Part 1 Total:" + total);

            //Part2
            string SourcePart2 = "";
            Int32[] array2 = SourcePart2.Select(x => Int32.Parse(x.ToString())).ToArray();
            int stepLengthOfArray = array2.Length / 2;
            int part2Total = 0;
            int b = 0;
            int pos = 0;
            foreach (var item in array2)
            {
                if ((b + stepLengthOfArray) < array2.Length)
                {
                    part2Total = item == array2[b + stepLengthOfArray] ? part2Total + item : part2Total;

                    //Console.WriteLine("item:" + item);
                    //Console.WriteLine("cp with :" + Array2[a + StepLengthOfArray]);

                }
                else
                {
                    pos = b + stepLengthOfArray - array2.Length;
                    part2Total = item == array2[pos] ? part2Total + item : part2Total;

                    //Console.WriteLine("item:" + item);
                    //Console.WriteLine("cp with:" + Array2[pos]);
                }
                b = b + 1;
               
            }
            Console.WriteLine("Part 2 Total:" + part2Total);
            Console.ReadKey();


        }
    }
}